		)
	   VALUES
	    (
	    	#{employeeId}

	      , #{firstName}

	      , #{lastName}
	      , #{email}



	      		, SYSDATE


	      		, #{hireDate}


	      , #{jobId}

	      , #{departmentId} 

	    )

